sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	AppSetProperty('active','true'); 
	//AppSetProperty('tables.incident.active','true'); 
	//AppSetProperty('tables.incident.columns.whitelist_trigger.sys_user_group','assignment_group'); 
	//AppSetProperty('tables.incident.columns.instant_access.sys_user','opened_by,caller_id'); 
	
	
	var testdata = new x_snc_datasilos.DataSilosTestDataAPI('_ATF_DATASILOS_');

	var strUserSentinel = testdata.CreateUser('SENTINEL',['itil','x_snc_datasilos.sentinel']);
	var strUserSpectator = testdata.CreateUser('SPECTATOR',[]);
	var strUserItil = testdata.CreateUser('ITIL',['itil']);
	var strUserAlphaMember = testdata.CreateUser('ALPHA_MEMBER',['itil']);
	var strUserAlphaFulfiller = testdata.CreateUser('ALPHA_FULFILLER',['itil']);
	var strUserAlphaSupervisor = testdata.CreateUser('ALPHA_SUPERVISOR',['itil']);
	var strGroupAlphaMember = testdata.CreateGroup('ALPHA_MEMBER',['ALPHA_MEMBER']);
	var strGroupAlphaFulfiller = testdata.CreateGroup('ALPHA_FULFILLER',['ALPHA_FULFILLER']);
	var strGroupAlphaSupervisor = testdata.CreateGroup('ALPHA_SUPERVISOR',['ALPHA_SUPERVISOR']);
	var strSiloAlpha = testdata.CreateSilo('ALPHA',['ALPHA_MEMBER'],['ALPHA_FULFILLER'],['ALPHA_SUPERVISOR']);
	

	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	
	var grCI = new GlideRecord('cmdb_ci');
	grCI.initialize();
	grCI.name = testdata.GetPrefix()+'CI';
	x_snc_datasilos.RecordSetSupervisorWhitelistValue(grCI,strSiloAlpha);
	//x_snc_datasilos.RecordSetFulfillerWhitelistValue(grCI,strSiloAlpha);
	var strCI = grCI.insert();

	var grServerCI = new GlideRecord('cmdb_ci_server');
	grServerCI.initialize();
	grServerCI.name = testdata.GetPrefix()+'CI_SERVER';
	x_snc_datasilos.RecordSetSupervisorWhitelistValue(grServerCI,strSiloAlpha);
	//x_snc_datasilos.RecordSetFulfillerWhitelistValue(grServerCI,strSiloAlpha);
	var strServerCI = grServerCI.insert();
	
	
	
	test.Log('Check a record in cmdb_ci via cmdb_ci\n');
	test.TestCase_IsRecordAccessibleByUser(strUserSentinel,       'cmdb_ci','',strCI,true);
	test.TestCase_IsRecordAccessibleByUser(strUserSpectator,      'cmdb_ci','',strCI,false);
	test.TestCase_IsRecordAccessibleByUser(strUserItil,           'cmdb_ci','',strCI,false);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaMember,    'cmdb_ci','',strCI,false);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaFulfiller, 'cmdb_ci','',strCI,false);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaSupervisor,'cmdb_ci','',strCI,true);
	
	test.Log('Check a record in cmdb_ci_server via cmdb_ci\n');
	test.TestCase_IsRecordAccessibleByUser(strUserSentinel,       'cmdb_ci','',strServerCI,true);
	test.TestCase_IsRecordAccessibleByUser(strUserSpectator,      'cmdb_ci','',strServerCI,false);
	test.TestCase_IsRecordAccessibleByUser(strUserItil,           'cmdb_ci','',strServerCI,false);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaMember,    'cmdb_ci','',strServerCI,false);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaFulfiller, 'cmdb_ci','',strServerCI,false);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaSupervisor,'cmdb_ci','',strServerCI,true);		

	test.Log('Check a record in cmdb_ci_server via cmdb_ci_server\n');
	test.TestCase_IsRecordAccessibleByUser(strUserSentinel,       'cmdb_ci_server','',strServerCI,true);
	test.TestCase_IsRecordAccessibleByUser(strUserSpectator,      'cmdb_ci_server','',strServerCI,false);
	test.TestCase_IsRecordAccessibleByUser(strUserItil,           'cmdb_ci_server','',strServerCI,false);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaMember,    'cmdb_ci_server','',strServerCI,false);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaFulfiller, 'cmdb_ci_server','',strServerCI,false);
	test.TestCase_IsRecordAccessibleByUser(strUserAlphaSupervisor,'cmdb_ci_server','',strServerCI,true);	

	test.Log(x_snc_devtools.RenderValue(testdata.GetAllData()));

	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
